import axios from "axios";

import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import SidebarLoader from "./SidebarLoader";


function Users() {
  let [products, setProducts] = useState([]);
  let [is, setLoaded] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      axios.get("https://fakestoreapi.com/products").then((res) => {
        setProducts(res.data);
        setLoaded(true);

      }, (err) => {
       
        setLoaded(true);
      });


    }, 2000);

  }, []);

  if(load) {
    return (
      <>
        <h1>Welcome</h1>
        <div className="container">
          <div className="row">
            {
              products.map((data) => {

                return <div className="card col-lg-4 mt-2" >
                  <Link to={"/product/" + data.id} style={{ color: "black", textDecoration: 'none' }} >
                    <img className="card-img-top" src={data.image} alt="Card image cap" />
                    <div className="card-body">

                      <p className="card-text"></p>

                    </div>
                    <ul className="list-group list-group-flush">
                      <li className="list-group-item "><b>Title:</b>{data.title}</li>
                      <li className="list-group-item "><b>Category:</b>{data.category}</li>
                      <li className="list-group-item"><b>Description:</b>{data.description}</li>
                      <li className="list-group-item"><b>Rating:</b>{data.rating.count}</li>
                      <li className="list-group-item"><b>Price:</b>{data.price}</li>
                    </ul>

                  </Link>
                </div>

              })
            }
          </div>
        </div>

      </>
    );
  }
  else {
    return
    <div>
      <SidebarLoader />
    </div>
  }
}

export default Users;
