import axios from "axios";
import React from "react";
import  { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Product(){
    let [products, setProducts] = useState([]);
  
  useEffect(() => {
    axios.get("https://fakestoreapi.com/products/1").then((res) => {
      console.log(res.data);
      setProducts(res.data);
    }, (err) => {
      console.log(err);
    });

  }, []);

    return(
        <>
          <h1>Welcome product</h1>
      <div className="container">
        <div className="row">
        {
          products.map((data)=>{
        
          return <div className="card col-lg-4 mt-2">
            <Link to={"/product/" +data.id}></Link>
            <img className="card-img-top" src={data.image} alt="Card image cap" />
            <div className="card-body">
              
              <p className="card-text"></p>
        
            </div>
            <ul className="list-group list-group-flush">
              <li className="list-group-item "><b>Title:</b>{data.title}</li>
              <li className="list-group-item "><b>Category:</b>{data.category}</li>
              <li className="list-group-item"><b>Description:</b>{data.description}</li>
              <li className="list-group-item"><b>Rating:</b>{data.rating.count}</li>
              <li className="list-group-item"><b>Price:</b>{data.price}</li>
            </ul>

            
          </div>
          
          })
        }
        </div>
        </div>
        </>
    )
}
export default Product;
