  let [load, setLoaded] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      axios.get("https://fakestoreapi.com/products").then((res) => {
        setProducts(res.data);
        setLoaded(true);

      }, (err) => {
       
        setLoaded(true);
      });


    }, 2000);

  }, []);

  if(load) {
    return (
      <>
        <h1>Welcome</h1>
